/* Demo for FinTech@SG Course 
Generation of Charts Based on JSON data from Server
Author: Prof Bhojan Anand */
//Install d3.js:   npm install d3 --save
import React from "react";
import logo from "./brand-logo.png";
import * as d3 from 'd3';

import "./App.css";



function sortTable(n) {
  var table, rows, switching, i, x, y, shouldSwitch, dir, switchcount = 0;
  table = document.getElementById("table");
  switching = true;
  dir = "asc";

  while (switching) {
      switching = false;
      rows = table.rows;

      for (i = 1; i < (rows.length - 1); i++) {
          shouldSwitch = false;
          x = rows[i].getElementsByTagName("TD")[n];
          y = rows[i + 1].getElementsByTagName("TD")[n];

          if (dir == "asc") {
              if (x.innerHTML.toLowerCase() > y.innerHTML.toLowerCase()) {
                  shouldSwitch = true;
                  break;
              }
          } else if (dir == "desc") {
              if (x.innerHTML.toLowerCase() < y.innerHTML.toLowerCase()) {
                  shouldSwitch = true;
                  break;
              }
          }
      }
      if (shouldSwitch) {
          rows[i].parentNode.insertBefore(rows[i + 1], rows[i]);
          switching = true;
          switchcount ++;
      } else {
          if (switchcount == 0 && dir == "asc") {
              dir = "desc";
              switching = true;
          }
      }
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      accounts: [],
      customers: [],
      transactions: []
     };

  }

  callAPIServer() {
    // when component mounted, start a GET request
    // to specified URL
    fetch("https://f92c6b35-d121-4c8a-987b-81217155297f.mock.pstmn.io/accounts")
      .then(res => res.text())
      .then(res => this.setState({ accounts: JSON.parse(res) }))
      .catch(err => err);

    fetch("https://f92c6b35-d121-4c8a-987b-81217155297f.mock.pstmn.io/customers")
      // when we get a response map the body to json
      .then(res => res.text())
      .then(res => this.setState({ customers: JSON.parse(res) }))
      .catch(err => err);

    fetch("https://f92c6b35-d121-4c8a-987b-81217155297f.mock.pstmn.io/transactions")
      // when we get a response map the body to json
      .then(res => res.text())
      .then(res => this.setState({ transactions: JSON.parse(res) }))
      .catch(err => err);

    }

  componentDidMount() {
    // react lifecycle method componentDidMount()
    //will execute the callAPIserver() method after the component mounts.
    this.callAPIServer();
  }


 componentDidUpdate() {

  /* prepare data */
  console.log(this.state.accounts);
  this.state.accounts.forEach(function (d) {
    d.balance = d.balance.replace(/[^0-9.-]+/g,"");  //regular expression to convert currency to Numeric form
  });
  console.log(this.state.accounts);

  this.generateGraph();  //based on previous d3.js exampls
  this.showChart();  //improved version way to chart

}

showChart() {


  const margin = { top: 50, right: 50, bottom: 50, left: 50 };
  var width = 1000 - margin.left - margin.right;
  var height = 500 - margin.top - margin.bottom;
  var svg = d3.select("#barChart")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom)
    .append("g")
    .attr("transform", "translate(" + margin.left + "," + margin.top + ")")

  svg.selectAll("*").remove();

  var x = d3.scaleBand().range([0, width]);
  var y = d3.scaleLinear().range([height, 0]);

  x.domain(this.state.accounts.map(details => details.account));
  y.domain([0, d3.max(this.state.accounts.map(details => details.balance))]);



  svg.selectAll(".bar")
    .data(this.state.accounts)
    .enter()
    .append("rect")
    .attr("class", "bar")
    .attr("x", d => x(d.account))
    .attr("width", x.bandwidth() - 10)
    .attr("y", d => y(d.balance))
    .attr("height", d => height - y(d.balance));

  svg.append("g")
    .attr("transform", "translate(0," + height + ")")
    .call(d3.axisBottom(x))

  svg.append("g")
    .call(d3.axisLeft(y));
}

  generateGraph() {

    var maxVal = d3.max(this.state.accounts.map(details => Number(details.balance)));
    console.log(maxVal);
    var svg = d3
      .select("#visualisation")
      .append("svg")
      .attr("width", 500)
      .attr("height", 200);

    svg
      .selectAll("rect")
      .data(this.state.accounts)
      .enter()
      .append("rect")
      .attr("transform", function (d, i) {
        return "translate(" + 60 + "," + i * 25 + ")";
      })
      .attr("fill", "blue")
      .attr("height", 20)
      .attr("width", function (d) {
        return d.balance /maxVal * 500 + "px";
      });

    svg
      .selectAll("text")
      .data(this.state.accounts)
      .enter()
      .append("text")
      .attr("transform", function (d, i) {
        return "translate(0," + Number(i * 25 + 15) + ")";
      })
      .attr("fill", "red")
      .text(function (d) {
        return d.account;
      });


  } 





  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">NUSmoney App+</h1>
        </header>

        <table className="myTable">
          <tbody>
          {this.state.accounts.map((item) => {
            return (
              <tr key={item.id}>
                <td> {item.account} </td>
                <td> {item.balance} </td>
              </tr>
            );
          })}
          </tbody>
        </table>

        <table className="myTable">
          <tbody>
          {this.state.customers.map((item) => {
            return (
              <tr key={item.id}>
                  <td> {item.first_name} </td>
                  <td> {item.last_name} </td>
                  <td> {item.email}  </td>
                  <td> {item.gender} </td>
              </tr>
            );
          })}
          </tbody>
        </table>

        <table className="myTable">
        <tr>
        <th onClick= "sortTable(0)" >timestamp</th>
        <th onClick= "sortTable(1)" >category</th>
        <th onClick= "sortTable(2)" >account</th>
        <th onClick= "sortTable(4)" >amount</th>
      </tr>
          <tbody>
          {this.state.transactions.map((item) => {
            return (
              <tr key={item.transaction_id}>
                  <td> {item.timestamp} </td>
                  <td> {item.category} </td>
                  <td> {item.account}  </td>
                  <td> {item.amount} </td>
              </tr>
            );
          })}
          </tbody>
        </table>


        <h2> Visualisation of Accounts</h2>
        <div id="visualisation">
            <svg id="barChart"></svg>
          </div>
      </div>







    );
  }
}

export default App;
