  editcreditlist(){
    const creditlist = [...this.state.tcredit];
    const updatedlist = new Array();
    creditlist.forEach(function(item){
      if(item.account == "credit"){
        updatedlist.push(item);
      }
    });
    this.setState({tcredit: updatedlist});
  }

  newcreditlist(){
    const creditlist = [...this.state.tcredit];
    const updatedlist = [{account: "credit", clothing: "0", utilities: "0", recreation: "0", groceries: "0", salary: "0", housing: "0", insurance: "0"}];
    creditlist.forEach(function(item){
      if(item.category == "clothing"){
        updatedlist[0].clothing = Number(updatedlist[0].clothing) + Number(item.amount);  
      }
      else if(item.category == "utilities"){
        updatedlist[0].utilities = Number(updatedlist[0].utilities) + Number(item.amount);
      }
      else if(item.category == "recreation"){
        updatedlist[0].recreation = Number(updatedlist[0].recreation) + Number(item.amount);
      }
      else if(item.category == "groceries"){
        updatedlist[0].groceries = Number(updatedlist[0].groceries) + Number(item.amount);
      }
      else if(item.category == "salary"){
        updatedlist[0].salary = Number(updatedlist[0].salary) + Number(item.amount);
      }
      else if(item.category == "housing"){
        updatedlist[0].housing = Number(updatedlist[0].housing) + Number(item.amount);
      }
      else if(item.category == "insurance"){
        updatedlist[0].insurance = Number(updatedlist[0].insurance) + Number(item.amount);
      }
