  showChart() {
    const margin = {
      top: 50,
      right: 50,
      bottom: 50,
      left: 50
    };
    var width = 1000 - margin.left - margin.right;
    var height = 500 - margin.top - margin.bottom;
    var svg = d3.select("#barChart").attr("width", width + margin.left + margin.right).attr("height", height + margin.top + margin.bottom).append("g").attr("transform", "translate(" + margin.left + "," + margin.top + ")");
    svg.selectAll("*").remove();
    var x = d3.scaleBand().range([0, width]);
    var y = d3.scaleLinear().range([height, 0]);
    x.domain(this.state.accounts.map(details => details.account));
    y.domain([0, d3.max(this.state.accounts.map(details => details.balance))]);
    svg.selectAll(".bar").data(this.state.accounts).enter().append("rect").attr("class", "bar").attr("x", d => x(d.account)).attr("width", x.bandwidth() - 10).attr("y", d => y(d.balance)).attr("height", d => height - y(d.balance));
    svg.append("g").attr("transform", "translate(0," + height + ")").call(d3.axisBottom(x));
    svg.append("g").call(d3.axisLeft(y));
  }

