  divergingchequechart(){

    x = d3.scaleLinear()
    .domain(d3.extent(this.state.tcredit, d => d.value))
    .rangeRound([margin.left, width - margin.right]);

    y = d3.scaleBand()
    .domain(d3.range(this.state.tcredit.length))
    .rangeRound([margin.top, height - margin.bottom])
    .padding(0.1);

    xAxis = g => g
    .attr("transform", `translate(0,${margin.top})`)
    .call(d3.axisTop(x).ticks(width / 80).tickFormat(tickFormat))
    .call(g => g.select(".domain").remove());

    yAxis = g => g
    .attr("transform", `translate(${x(0)},0)`)
    .call(d3.axisLeft(y).tickFormat(i => this.state.tcredit[i].name).tickSize(0).tickPadding(6))
    .call(g => g.selectAll(".tick text").filter(i => this.state.tcredit[i].value < 0)
        .attr("text-anchor", "start")
        .attr("x", 6));

    format = d3.format(metric === "absolute" ? "+,d" : "+,.0%");
    tickFormat = metric === "absolute" ? d3.formatPrefix("+.1", 1e6) : format;
    barHeight = 25
    height = Math.ceil((this.state.tcredit.length + 0.1) * barHeight) + margin.top + margin.bottom
    margin = ({top: 30, right: 60, bottom: 10, left: 60})
    d3 = require("d3@6")

    const svg = d3
      .select("#bar")
      .create("svg")
      .attr("viewBox", [0, 0, width, height]);

    svg.append("g")
      .selectAll("rect")
      .data(this.state.tcredit)
      .join("rect")
        .attr("fill", d => d3.schemeSet1[d.value > 0 ? 1 : 0])
        .attr("x", d => x(Math.min(d.value, 0)))
        .attr("y", (d, i) => y(i))
        .attr("width", d => Math.abs(x(d.value) - x(0)))
        .attr("height", y.bandwidth());

    svg.append("g")
        .attr("font-family", "sans-serif")
        .attr("font-size", 10)
      .selectAll("text")
      .data(this.state.tcredit)
      .join("text")
        .attr("text-anchor", d => d.value < 0 ? "end" : "start")
        .attr("x", d => x(d.value) + Math.sign(d.value - 0) * 4)
        .attr("y", (d, i) => y(i) + y.bandwidth() / 2)
        .attr("dy", "0.35em")
        .text(d => format(d.value));

    svg.append("g")
        .call(xAxis);

    svg.append("g")
        .call(yAxis);

    return svg.node();
  }

